    private function lat($str): string
    {
        return str_replace($this->cyr, $this->lat, $str);
    }

    private function generateDataChunksSQL($start, $number): void
    {
        $data = [
            self::T_AUTHOR => [],
            self::T_AUTHOR_TRANSLATION => [],
            self::T_BOOK => [],
            self::T_BOOK_TRANSLATION => [],
            self::T_AUTHOR_BOOK => []
        ];

        $end = $start + $number;

        for ($id = $start; $id < $end; $id++) {
            $data['ids'][] = '(' . $id . ')';

            $authorName = $this->authors[array_rand($this->authors)]
                . ' '
                . $this->names[array_rand($this->names)]
                . $this->names[array_rand($this->names)];

            $data[self::T_AUTHOR_TRANSLATION][] = "($id,'$authorName','ru')";
            $authorName = $this->lat($authorName);
            $data[self::T_AUTHOR_TRANSLATION][] = "($id,'$authorName','en')";

            $bookName = $this->words[array_rand($this->words)];
            $bookName = mb_convert_case($bookName, MB_CASE_TITLE, "UTF-8");
            $data[self::T_BOOK_TRANSLATION][] = "($id,'$bookName','ru')";
            $bookName = $this->lat($bookName);
            $data[self::T_BOOK_TRANSLATION][] = "($id,'$bookName','en')";

            $data[self::T_AUTHOR_BOOK][] = "($id,$id)";
        }

        $idsPack = implode(',', $data["ids"]);
        $this->addSql('INSERT INTO ' . self::T_AUTHOR . ' (id) VALUES ' . $idsPack . ';');
        $this->addSql('INSERT INTO ' . self::T_AUTHOR_TRANSLATION . ' (translatable_id,name,locale) VALUES ' . implode(',', $data[self::T_AUTHOR_TRANSLATION]) . ';');
        $this->addSql('INSERT INTO ' . self::T_BOOK . ' (id) VALUES ' . $idsPack . ';');
        $this->addSql('INSERT INTO ' . self::T_BOOK_TRANSLATION . ' (translatable_id,name,locale) VALUES ' . implode(',', $data[self::T_BOOK_TRANSLATION]) . ';');
        $this->addSql('INSERT INTO ' . self::T_AUTHOR_BOOK . ' (author_id,book_id) VALUES ' . implode(',', $data[self::T_AUTHOR_BOOK]) . ';');
    }

